#ifndef MEMMAP_H
#define MEMMAP_H

// =============================================
// The following is allocation for static memory
// For lookup table
// align with 64 bytes
#define SFU_TAYLOR_TABLE_SIZE 32
#define SFU_TAYLOR_L_TABLE_SIZE 64
#define ERF_TAYLOR_SIZE 16
#define STATIC_MEM_OFFSET 0
#define SERIAL_NUMBER_SIZE 64
#define SIN_TAYLOR_SIZE 32
#define COS_TAYLOR_SIZE 32
#define ARCSIN_TAYLOR_SIZE 64
#define TAN_TAYLOR_SIZE 32
#define DIV_BFP16_LUT_SIZE 128

#define EXP_BF16_TAYLOR_OFFSET (STATIC_MEM_OFFSET)
#define LOG_BF16_TAYLOR_OFFSET (EXP_BF16_TAYLOR_OFFSET + ERF_TAYLOR_SIZE * sizeof(short))
#define ERF_BF16_TAYLOR_OFFSET (LOG_BF16_TAYLOR_OFFSET + ERF_TAYLOR_SIZE * sizeof(short))
#define SERIAL_NUMBER_OFFSET (ERF_BF16_TAYLOR_OFFSET + ERF_TAYLOR_SIZE * sizeof(short))
#define SERIAL_INT16_NUMBER_OFFSET (SERIAL_NUMBER_OFFSET + SERIAL_NUMBER_SIZE * sizeof(int))
#define SIN_BFP16_TAYLOR_OFFSET (SERIAL_INT16_NUMBER_OFFSET + SERIAL_NUMBER_SIZE * sizeof(short))
#define COS_BFP16_TAYLOR_OFFSET (SIN_BFP16_TAYLOR_OFFSET + SIN_TAYLOR_SIZE * sizeof(short))
#define ARCSIN_BFP16_TAYLOR_OFFSET (COS_BFP16_TAYLOR_OFFSET + COS_TAYLOR_SIZE * sizeof(short))
#define DIV_BFP16_LUT_OFFSET (ARCSIN_BFP16_TAYLOR_OFFSET + ARCSIN_TAYLOR_SIZE * sizeof(short))
#define SMEM_STATIC_END_OFFSET (DIV_BFP16_LUT_OFFSET + DIV_BFP16_LUT_SIZE * sizeof(short))
#endif
